merge into tmp_depart t
using (
select t.code,t.name,d.id dep_id
from tmp_depart t left join department d on (upper(replace(d.name,'ё','е'))=upper(t.name) or
upper(d.shortname)=upper(t.code))
) v
on (t.code=v.code)
when matched then update set t.dep_id=v.dep_id;
end;

BEGIN
merge into ref_book_oktmo a using (select '27714000' as code, 'Населенные пункты, входящие в состав Неманского городского округа' as name, 2 as razd, to_date('01.03.2017','dd.mm.yyyy') as version from dual union all select '27703000' as code, 'Населенные пункты, входящие в состав Багратионовского городского округа' as name, 2 as razd, to_date('01.03.2017','dd.mm.yyyy') as version from dual union all select '27718000' as code, 'Населенные пункты, входящие в состав Полесского городского округа' as name, 2 as razd, to_date('01.03.2017','dd.mm.yyyy') as version from dual union all select '49625405' as code, 'Тёсово-Нетыльское' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46729000' as code, 'Зарайск' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46733000' as code, 'Истра' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46744000' as code, 'Красногорск' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46747000' as code, 'Луховицы' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46748000' as code, 'Люберцы' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46750000' as code, 'Наро-Фоминский' as name, 1 as razd, to_date('01.03.2018','dd.mm.yyyy') as version from dual union all select '46759000' as code, 'Павловский Посад' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46766000' as code, 'Рузский' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '46776000' as code, 'Ступино' as name, 1 as razd, to_date('01.03.2018','dd.mm.yyyy') as version from dual union all select '46784000' as code, 'Чехов' as name, 1 as razd, to_date('01.03.2018','dd.mm.yyyy') as version from dual union all select '46786000' as code, 'Шатура' as name, 1 as razd, to_date('01.03.2018','dd.mm.yyyy') as version from dual union all select '28752000' as code, 'Населенные пункты, входящие в состав Осташковского городского округа' as name, 2 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual ) b on (a.code=b.code and a.status=0) when not matched then insert (id, record_id, version, status, code, name, razd) values (seq_ref_book_record.nextval,seq_ref_book_record_row_id.nextval, b.version, 0, b.code, b.name, b.razd);
update ref_book_oktmo set name='Неманский (г Неман)', razd=1 where code='27714000' and status=0; update ref_book_oktmo set name='Багратионовский', razd=1 where code='27703000' and status=0; update ref_book_oktmo set name='Полесский (г Полесск)', razd=1 where code='27718000' and status=0;
merge into ref_book_oktmo a using (select '22739000' as code, 'Перевозский (г Перевоз)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '41648165' as code, 'Фёдоровское (гп Фёдоровское)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07705000' as code, 'Благодарненский (г Благодарный)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07713000' as code, 'Изобильненский (г Изобильный)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07714000' as code, 'Ипатовский (г Ипатово)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07716000' as code, 'Кировский (г Новопавловск)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07725000' as code, 'Нефтекумский (г Нефтекумск)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07731000' as code, 'Петровский (г Светлоград)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07735000' as code, 'Советский (г Зеленокумск)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '41630152' as code, 'Аннинское (гп Новоселье)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '07726000' as code, 'Новоалександровский (г Новоалександровск)' as name, 1 as razd, to_date('01.01.2018','dd.mm.yyyy') as version from dual union all select '26720000' as code, 'город Сунжа (г Сунжа)' as name, 1 as razd, to_date('01.01.2017','dd.mm.yyyy') as version from dual ) b on (a.code=b.code and a.status=0) when not matched then insert (id, record_id, version, status, code, name, razd) values (seq_ref_book_record.nextval,seq_ref_book_record_row_id.nextval, b.version, 0, b.code, b.name, b.razd);
END;

declare v_version date:=to_date('01.01.2016','dd.mm.yyyy'); begin delete from ref_book_ndfl_detail; delete from ref_book_ndfl;
for dep in (select depcode,count(*) cnt,max(row_num) last_num from tmp_dep_params group by depcode minus select td.code,count(*) cnt,max(row_ord) last_num from ref_book_ndfl n join department d on (d.id=n.department_id) join ref_book_ndfl_detail p on (p.ref_book_ndfl_id=n.id) join tmp_depart td on (td.dep_id=d.id) group by td.code order by 1) loop
insert into ref_book_ndfl(department_id, version, record_id, status, id) select tab.*,seq_ref_book_record_row_id.nextval,0,seq_ref_book_record.nextval from (select d.dep_id, v_version from tmp_dep_params t join tmp_depart d on (d.code=t.depcode) where d.dep_id is not null and t.depcode=dep.depcode group by d.dep_id,t.depcode,t.inn) tab; end loop;
insert into ref_book_ndfl_detail(department_id, id, ref_book_ndfl_id,record_id, version, status, row_ord, tax_organ_code, kpp, present_place, name, oktmo, phone, signatory_id, signatory_surname, signatory_firstname, signatory_lastname, approve_doc_name, approve_org_name, reorg_form_code, tax_organ_code_mid, okved, region, obligation, reorg_inn, reorg_kpp) select d.dep_id, seq_ref_book_record.nextval,n.id ndfl_id,seq_ref_book_record_row_id.nextval,v_version,0,row_num, t.tax_end,t.kpp,pp.id place_id,t.titname, o.mid oktmo_id,t.phone,s.id sign_id,t.surname,t.name,t.lastname,t.docname,t.orgname,ro.id reorg_id, t.tax_organ_code_mid, t.okved, t.region, t.obligation, t.reorg_inn, t.reorg_kpp from tmp_dep_params t join tmp_depart d on (d.code=t.depcode) left join ref_book_ndfl n on (n.department_id=d.dep_id and n.version=v_version) left join ref_book_present_place pp on (pp.code=t.place and pp.status=0) left join (select code,max(id) mid from ref_book_oktmo group by code) o on (o.code=t.oktmo) left join ref_book_signatory_mark s on (s.code=t.sign and v_version between s.version and v_version) left join ref_book_reorganization ro on (ro.code=t.reorgcode and v_version between ro.version and v_version);
end;

alter table ref_book_ndfl_detail modify row_ord number(9);
drop table tmp_depart;
drop table tmp_dep_params;

