--заполняем оставшиеся
select seq_ref_book_record.nextval,r.id,'00','-'
  from ref_book_income_type r
 where not exists (select 1 from ref_book_income_kind k where k.income_type_id=r.id)
   and r.status=0
   and r.version=(select max(version) from ref_book_income_type v where v.code=r.code);

